import C_BEISEN_PKG:RequestHandler;

/* 申请相关接口 */
class Apply
   property C_BEISEN_PKG:RequestHandler RequestHandler;

   /* 构造函数 */
   method Apply();

   /* 根据申请ID获取申请信息 */
   method getApplyListByApplyId(&applyIds As JsonArray, &fieldNames As JsonArray) Returns Message;
end-class;


method Apply
   &RequestHandler = create C_BEISEN_PKG:RequestHandler();
end-method;


method getApplyListByApplyId
   /+ &applyIds as JsonArray, +/
   /+ &fieldNames as JsonArray +/
   /+ Returns Message +/
   Local Message &respMessage;

   REM URL;
   Local string &url = "/RecruitV6/api/v1/Apply/GetApplyListByApplyId";

   REM 请求报文;
   Local JsonObject &requestObject = CreateJsonObject();
   &requestObject.AddJsonArray("applyIds", &applyIds);
   &requestObject.AddJsonArray("fieldNames", &fieldNames);

   &respMessage = &RequestHandler.SendPostRequest(&url, &requestObject.ToString());
   Return &respMessage;
end-method;

import C_BEISEN_PKG:RequestHandler;

/* 入职管理相关接口 */
class RecruitOnBoarding
   property C_BEISEN_PKG:RequestHandler RequestHandler;

   /* 构造函数 */
   method RecruitOnBoarding();

   /* 查询offer信息 */
   method GetOfferInfos(&contentScene As integer, &items As JsonArray) Returns Message;

   /* 查询待入职人员信息 */
   method GetStaffInfos(&items As JsonArray) Returns Message;

   /* 获取offerId */
   method GetOfferIds(&approvalState As string, &offerState As string, &isModify As boolean, &startTime As datetime, &endTime As datetime, &batchId As string) Returns Message;

   /* 获取待入职员工Id */
   method GetStaffIds(&staffStatus As string, &infoCollectionStatus As string, &isModify As boolean, &startTime As datetime, &endTime As datetime, &batchId As string) Returns Message;
end-class;


method RecruitOnBoarding
   &RequestHandler = create C_BEISEN_PKG:RequestHandler();
end-method;


method GetOfferInfos
   /+ &contentScene as Integer, +/
   /+ &items as JsonArray +/
   /+ Returns Message +/
   Local Message &respMessage;
   Local string &url;

   If &contentScene = 1 Then
      &url = "/RecruitV6/api/v1/RecruitOnBoarding/GetOfferInfos?contentScene=1";
   Else
      &url = "/RecruitV6/api/v1/RecruitOnBoarding/GetOfferInfos";
   End-If;

   &respMessage = &RequestHandler.SendPostRequest(&url, &items.ToString());

   Return &respMessage;
end-method;


method GetStaffInfos
   /+ &items as JsonArray +/
   /+ Returns Message +/
   Local Message &respMessage;

   Local string &url = "/RecruitV6/api/v1/RecruitOnBoarding/GetStaffInfos";

   &respMessage = &RequestHandler.SendPostRequest(&url, &items.ToString());

   Return &respMessage;
end-method;


method GetOfferIds
   /+ &approvalState as String, +/
   /+ &offerState as String, +/
   /+ &isModify as Boolean, +/
   /+ &startTime as DateTime, +/
   /+ &endTime as DateTime, +/
   /+ &batchId as String +/
   /+ Returns Message +/
   Local Message &respMessage;
   Local string &url = "/RecruitV6/api/v1/RecruitOnBoarding/GetOfferIds";

   /* 填充请求报文 bgn */
   Local JsonObject &requestObject = CreateJsonObject();

   REM 审批状态;
   If All(&approvalState) Then
      &requestObject.AddProperty("approvalState", Value(&approvalState));
   End-If;

   REM Offer状态;
   If All(&offerState) Then
      &requestObject.AddProperty("offerState", Value(&offerState));
   End-If;

   REM 是否按修改时间查询;
   &requestObject.AddProperty("isModify", &isModify);

   REM 开始时间;
   &requestObject.AddProperty("startTime", DateTimeToLocalizedString(&startTime, "yyyy-MM-dd'T'HH:mm:ss"));

   REM 结束时间;
   &requestObject.AddProperty("endTime", DateTimeToLocalizedString(&endTime, "yyyy-MM-dd'T'HH:mm:ss"));

   REM 批次ID;
   &requestObject.AddProperty("batchId", &batchId);
   /* 填充请求报文 end */

   REM 发送请求;
   &respMessage = &RequestHandler.SendPostRequest(&url, &requestObject.ToString());

   Return &respMessage;
end-method;


method GetStaffIds
   /+ &staffStatus as String, +/
   /+ &infoCollectionStatus as String, +/
   /+ &isModify as Boolean, +/
   /+ &startTime as DateTime, +/
   /+ &endTime as DateTime, +/
   /+ &batchId as String +/
   /+ Returns Message +/
   Local Message &respMessage;

   Local string &url = "/RecruitV6/api/v1/RecruitOnBoarding/GetStaffIds";

   /* 填充请求报文 bgn */
   Local JsonObject &requestObject = CreateJsonObject();

   REM 员工状态;
   If All(&staffStatus) Then
      &requestObject.AddProperty("staffStatus", Value(&staffStatus));
   End-If;

   REM 员工信息采集状态;
   If All(&infoCollectionStatus) Then
      &requestObject.AddProperty("infoCollectionStatus", Value(&infoCollectionStatus));
   End-If;

   REM 是否按修改时间查询;
   If &isModify Then
      &requestObject.AddProperty("isModify", True);
   Else
      &requestObject.AddProperty("isModify", False);
   End-If;

   REM 结束时间;
   &requestObject.AddProperty("endTime", DateTimeToLocalizedString(&endTime, "yyyy-MM-dd'T'hh:mm:ss"));

   REM 开始时间;
   &requestObject.AddProperty("startTime", DateTimeToLocalizedString(&startTime, "yyyy-MM-dd'T'hh:mm:ss"));

   REM 批次ID;
   If All(&batchId) Then
      &requestObject.AddProperty("batchId", &batchId);
   End-If;
   /* 填充请求报文 end */

   &respMessage = &RequestHandler.SendPostRequest(&url, &requestObject.ToString());
   Return &respMessage;
end-method;

