class JsonHandler
   /* 构造函数 */
   method JsonHandler();

   /* 获取北森的响应报文-JsonObject */
   method GetResponseObjectData(&ResMsg As Message) Returns JsonObject;

   /* 获取北森的响应报文-JsonArray */
   method GetResponseArrayData(&ResMsg As Message) Returns JsonArray;

   /* 获取扩展字段信息-ByText */
   method GetExtendInfoByText(&extendInfos As JsonArray, &Name As string) Returns string;

   /* 获取扩展字段信息-ByValue */
   method GetExtendInfoByValue(&extendInfos As JsonArray, &Name As string) Returns any;
end-class;


method JsonHandler

end-method;


method GetResponseObjectData
   /+ &ResMsg as Message +/
   /+ Returns JsonObject +/
   Local JsonObject &ResponseData;
   If &ResMsg.HTTPResponseCode = %IB_Status_Success Then
      Local JsonParser &Jparser = CreateJsonParser();
      If &Jparser.Parse(&ResMsg.GetContentString()) Then
         &ResponseData = &Jparser.GetRootObject().GetJsonObject("data");
      Else
         throw CreateException(0, 0, "解析Json失败，报文:" | &ResMsg.GetContentString());
      End-If;
   Else
      throw CreateException(0, 0, "状态码:" | &ResMsg.HTTPResponseCode | Char(10) | "报文:" | &ResMsg.GetContentString());
   End-If;

   Return &ResponseData;
end-method;


method GetResponseArrayData
   /+ &ResMsg as Message +/
   /+ Returns JsonArray +/
   Local JsonArray &ResponseData;
   If &ResMsg.HTTPResponseCode = %IB_Status_Success Then
      Local JsonParser &Jparser = CreateJsonParser();
      If &Jparser.Parse(&ResMsg.GetContentString()) Then
         &ResponseData = &Jparser.GetRootObject().GetJsonArray("data");
      Else
         throw CreateException(0, 0, "解析Json失败，报文:" | &ResMsg.GetContentString());
      End-If;
   Else
      throw CreateException(0, 0, "状态码:" | &ResMsg.HTTPResponseCode | Char(10) | "报文:" | &ResMsg.GetContentString());
   End-If;

   Return &ResponseData;
end-method;


method GetExtendInfoByText
   /+ &extendInfos as JsonArray, +/
   /+ &Name as String +/
   /+ Returns String +/
   Local integer &i;
   Local string &Text;
   For &i = 1 To &extendInfos.Length()
      Local JsonObject &extendInfo = &extendInfos.GetJsonObject(&i);
      If &extendInfo.GetString("name") = &Name Then
         /* IsNullProperty有BUG,不管这个属性是不是空的都会返回True,换JsonNode来判断 */
         REM If &extendInfo.IsNullProperty("text") Then ;
         Local JsonNode &node = &extendInfo.GetJsonNode("text");
         If &node.GetValueType() = 6 Then
            &Text = &extendInfo.GetString("text");
         End-If;
         Break;
      End-If;
   End-For;
   Return &Text;
end-method;


method GetExtendInfoByValue
   /+ &extendInfos as JsonArray, +/
   /+ &Name as String +/
   /+ Returns Any +/
   Local integer &i;
   Local any &Value = Null;
   For &i = 1 To &extendInfos.Length()
      Local JsonObject &extendInfo = &extendInfos.GetJsonObject(&i);
      If &extendInfo.GetString("name") = &Name Then
         /* IsNullProperty有BUG,不管这个属性是不是空的都会返回True,换JsonNode来判断 */
         REM If &extendInfo.IsNullProperty("value") Then ;
         Local JsonNode &node = &extendInfo.GetJsonNode("value");
         Evaluate &node.GetValueType()
         When 0
            REM 无效的Json;
            Break;
         When 1
            &Value = &extendInfo.GetInteger("value");
            Break;
         When 2
            &Value = &extendInfo.GetFloat("value");
            Break;
         When 3
            REM Double;
            &Value = &extendInfo.GetNumber("value");
            Break;
         When 4
            REM Number;
            &Value = &extendInfo.GetNumber("value");
            Break;
         When 5
            &Value = &extendInfo.GetBoolean("value");
            Break;
         When 6
            &Value = &extendInfo.GetString("value");
            Break;
         When 7
            REM Null;
            Break;
         When 8
            &Value = &extendInfo.GetDate("value");
            Break;
         When 9
            &Value = &extendInfo.GetTime("value");
            Break;
         When 10
            &Value = &extendInfo.GetDateTime("value");
            Break;
         End-Evaluate;
         Break;
      End-If;
   End-For;
   Return &Value;
end-method;

import C_BEISEN_PKG:Token;

class RequestHandler
   /* 构造函数 */
   method RequestHandler();

   /* 发送请求 */
   method SendPostRequest(&path As string, &requestBody As string) Returns Message;
end-class;

Global C_BEISEN_PKG:Token &token;

method RequestHandler
   If &token = Null Then
      &token = create C_BEISEN_PKG:Token();
   End-If;
end-method;


method SendPostRequest
   /+ &path as String, +/
   /+ &requestBody as String +/
   /+ Returns Message +/

   Local Message &reqMessage, &respMessage;
   Local boolean &bool;

   Local string &url = GetURL(URL.UIH_BEISEN) | &path;

   /* 如果Token过期了需要刷新Token */
   If (%Datetime - &token.create_time) > &token.expires_in Then
      &token.getToken();
   End-If;

   /* 发送请求 */
   &reqMessage = CreateMessage(Operation.IB_GENERIC_REST_POST, %IntBroker_Request);
   &reqMessage.URIResourceIndex = 1;
   &bool = &reqMessage.IBInfo.LoadRESTHeaders();
   &reqMessage.IBInfo.IBConnectorInfo.ConnectorName = "HTTPTARGET";
   &reqMessage.IBInfo.IBConnectorInfo.ConnectorClassName = "HttpTargetConnector";

   &bool = &reqMessage.IBInfo.IBConnectorInfo.AddConnectorProperties("Method", "POST", %HttpProperty);
   &bool = &reqMessage.IBInfo.IBConnectorInfo.AddConnectorProperties("URL", &url, %HttpProperty);
   &bool = &reqMessage.IBInfo.IBConnectorInfo.AddConnectorProperties("Content-Type", "application/json;charset=UTF-8", %Header);
   &bool = &reqMessage.IBInfo.IBConnectorInfo.AddConnectorProperties("Authorization", &token.access_token, %Header);
   &bool = &reqMessage.SetContentString(&requestBody);
   &respMessage = %IntBroker.ConnectorRequest(&reqMessage);

   Return &respMessage;
end-method;

class Token
   /* 构造函数 */
   method Token();

   /* 获取Token */
   method getToken();

   /* Token */
   property string access_token;

   /* 获取时间 */
   property datetime create_time;

   /* 有效期 */
   property number expires_in;
end-class;


method Token
   %This.getToken();
end-method;


method getToken
   Local Message &request_Message, &response_Message;
   Local string &app_key, &app_secret, &responseText;
   Local boolean &bool;

   REM 获取参数;
   &app_key = MsgGetExplainText(30013, 1, "");
   &app_secret = MsgGetExplainText(30013, 2, "");
   Local JsonObject &jsonObj = CreateJsonObject();
   &jsonObj.AddProperty("grant_type", "client_credentials");
   &jsonObj.AddProperty("app_key", &app_key);
   &jsonObj.AddProperty("app_secret", &app_secret);

   REM 发送请求;
   &request_Message = CreateMessage(Operation.IB_GENERIC_REST_POST, %IntBroker_Request);
   &request_Message.URIResourceIndex = 1;
   &bool = &request_Message.IBInfo.LoadRESTHeaders();
   &request_Message.IBInfo.IBConnectorInfo.ConnectorName = "HTTPTARGET";
   &request_Message.IBInfo.IBConnectorInfo.ConnectorClassName = "HttpTargetConnector";

   &bool = &request_Message.IBInfo.IBConnectorInfo.AddConnectorProperties("Method", "POST", %HttpProperty);
   &bool = &request_Message.IBInfo.IBConnectorInfo.AddConnectorProperties("URL", GetURL(URL.UIH_BEISEN) | "/token", %HttpProperty);
   &bool = &request_Message.IBInfo.IBConnectorInfo.AddConnectorProperties("Content-Type", "application/json;charset=UTF-8", %Header);
   &bool = &request_Message.SetContentString(&jsonObj.ToString());
   &response_Message = %IntBroker.ConnectorRequest(&request_Message);

   REM 获取返回结果;
   &responseText = &response_Message.GetContentString();
   Local number &statusCode = &response_Message.HTTPResponseCode;
   If &response_Message.HTTPResponseCode = %IB_Status_Success Then
      REM 解析Json字符串;
      Local JsonParser &jsonParser = CreateJsonParser();
      If &jsonParser.Parse(&responseText) Then
         Local JsonObject &responseObject = &jsonParser.GetRootObject();

         &access_token = "Bearer " | &responseObject.GetString("access_token");
         &expires_in = &responseObject.GetNumber("expires_in");
         &create_time = %Datetime;
      End-If;
   Else
      throw CreateException(0, 0, "获取Token失败，状态码：" | &statusCode | "，响应报文：" | &responseText);
   End-If;

end-method;
